CREATE VIEW view_mthly_payments AS SELECT
    mthly_payments.id as id,
    mthly_payments.student_id as student_id,
    mthly_payments.amount_to_pay as amount_to_pay,
    mthly_payments.amount_paid as amount_paid,
    mthly_payments.month_nmb as month_nmb,
    mthly_payments.ref_payment as ref_payment,  
    mthly_payments.course_level as course_level,
    mthly_payments.course_simest as course_simest,
    mthly_payments.bank_name as bank_name,
    mthly_payments.deposit_nmb as deposit_nmb,
    enrolled_students.student_name AS student_name,
    enrolled_students.stundent_surname AS stundent_surname,
    mthly_payments.user_datetime AS user_datetime,
    mthly_payments.iduser AS iduser 
FROM
     mthly_payments
LEFT JOIN enrolled_students ON mthly_payments.student_id = enrolled_students.student_id;
